if not IsMouseButtonDown() then
	self.Thumb:SetTexCoord(0.96875, 1, 0.0625, 0);
end

Scripts.OnLeave:
if not self.isActive then
	self.Thumb:SetTexCoord(0.9375, 0.96875, 0.0625, 0);
end

